      out.write("\n");
      out.write("        // 设置表单提交事件\n");
      out.write("        function setupFormSubmit() {\n");
      out.write("            document.getElementById('bookForm').addEventListener('submit', function(e) {\n");
      out.write("                e.preventDefault();\n");
      out.write("                if (isEditing) {\n");
      out.write("                    updateBook();\n");
      out.write("                } else {\n");
