      out.write("        }\n");
      out.write("\n");
      out.write("        // 显示提示信息\n");
      out.write("        function showAlert(message, type = 'info') {\n");
      out.write("            const alertContainer = document.getElementById('alertContainer');\n");
      out.write("            const alertDiv = document.createElement('div');\n");
      out.write("            alertDiv.className = `alert alert-${type}`;\n");
      out.write("            alertDiv.textContent = message;\n");
      out.write("            \n");
      out.write("            alertContainer.innerHTML = '';\n");
      out.write("            alertContainer.appendChild(alertDiv);\n");
      out.write("            \n");
      out.write("            // 3秒后自动消失\n");
      out.write("            setTimeout(() => {\n");
      out.write("                alertDiv.remove();\n");
      out.write("            }, 3000);\n");
      out.write("        }\n");
      out.write("\n");
      out.write("        // 重置表单\n");
      out.write("        function resetForm() {\n");
      out.write("            document.getElementById('bookForm').reset();\n");
      out.write("            document.getElementById('bookId').value = '';\n");
      out.write("            isEditing = false;\n");
      out.write("            currentBookId = null;\n");
      out.write("            document.getElementById('submitText').textContent = '添加图书';\n");
      out.write("            document.getElementById('cancelBtn').style.display = 'none';\n");
      out.write("        }\n");
      out.write("\n");
      out.write("        // 取消编辑\n");
      out.write("        function cancelEdit() {\n");
      out.write("            resetForm();\n");
      out.write("        }\n");
      out.write("\n");
      out.write("        // 添加图书\n");
      out.write("        async function addBook() {\n");
      out.write("            const formData = new FormData(document.getElementById('bookForm'));\n");
      out.write("            const bookData = {\n");
      out.write("                type: formData.get('type'),\n");
      out.write("                name: formData.get('name'),\n");
      out.write("                description: formData.get('description')\n");
      out.write("            };\n");
      out.write("\n");
      out.write("            try {\n");
      out.write("                const response = await fetch('/books', {\n");
      out.write("                    method: 'POST',\n");
      out.write("                    headers: {\n");
      out.write("                        'Content-Type': 'application/json'\n");
